import React from 'react'

const Header = ({ course }) => { 
	return(
	<div>
		<h1>{course}</h1> 
	</div>
	)
}

const Content = ({ parts }) => {
	return(
		<>
		{parts.map( ({name, exercises, id}) => (
		
			<Part key={id} name={name} exercises={exercises}/>
		
		))}
		</>
	)
}

const Total = ({ parts }) => {
	
	const total = parts.reduce((accumulator, currentValue) => {
		
			console.log('what is happening', accumulator.exercises, currentValue.exercises)
			const sum = accumulator.exercises + currentValue.exercises
			return(sum)
		})			
	
	return(
		<p>
			<b> total of {total} exercises </b>
		</p>
	)
	
}


const Part = ({name, exercises}) => {
	return(
		<p>
			{name} {exercises}
		</p>
	)
}


const Course = ({ course }) => {
	
	
	return(
		<div>
		{/*easier to pass the direct properties of the object using dot notation*/}
		
		<Header course = {course.name}/>
		<Content parts={course.parts} />
		<Total parts={course.parts} /> 
		</div>
	)
}

export default Course
